"text/css">    .button {        padding: 5px 8px;        text-align: center;        font-size: 13px;    }</style><script src="<?php echo base_url()."assets/"; ?>js/jquery-1.12.4.js"></script><script>    $(document).ready(function(){        var now = new Date();        var day = ("0" + now.getDate()).slice(-2);        var month = ("0" + (now.getMonth() + 1)).slice(-2);        var today = now.getFullYear()+"-"+(month)+"-"+(day) ;        $('.todate').val(today);    });</script>