<?phpclass YearModel extends CI_Model{    public function __construct()    {        parent::__construct();    }    public function getYears()    {        return $this->db->get('year')->result();    }    public function getActiveYear()    {        $this->db->where('Active', 1);        return $this->db->get('year')->result();    }    public function getYearStatus($year)    {        $this->db->select('Active');        $this->db->where('year',$year);        return $this->db->get('year')->row();    }    public function AddNewYear()    {        $activeYear = $this->getActiveYear();        $yearId = $activeYear[0]->Id;        $Year = $activeYear[0]->year;        $newYear = $Year + 1;        $this->db->set('year',$newYear);        $this->db->set('Active',1);        $this->db->insert('year');        if($this->db->affected_rows() > 0){            $isDeactivated = $this->DeActivateYear($yearId);            if($isDeactivated){                return true;            }else{                return false;            }        }else{            return false;        }    }    public function DeActivateYear($yearId)    {        $this->db->set('Active',0);        $this->db->where('Id', $yearId);        $this->db->update('year');        if($this->db->affected_rows() > 0){            return true;        }else{            return false;        }    }}