<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class DashboardModel extends CI_Model{	public function save_data()	{		$this->db->insert('test', $_POST);		if($this->db->affected_rows() > 0)		{			return true;		}else{			return false;		}	}    public function UpdateDate()    {        $this->db->set('Qm_date','1438-11-02');        $this->db->where('Sh_date','2017-07-26');        $this->db->update('calender');	}	public function get_data()	{		$this->db->select('*');		$this->db->from('transactions');		$this->db->join('account_title', 'transactions.AccountID = account_title.id');		return $this->db->get()->result();	}}