').val();        post.lock = $('#elock').val();        $.ajax({            type:'POST',            url:'<?php echo site_url('Users/updateUser');?>'+'/'+id,            data:post,            success:function(response){                var data = $.parseJSON(response);                if(data['form_error']){                    $('.uname').text(data.username);                    $('.upass').text(data.password);                    $('.uconpass').text(data.confpass);                }                if(data['success']){                    new PNotify({                        title: 'کامیابی',                        text: 'اکاؤنٹ تدوین کامیاب',                        type: 'success',                        hide: true                    });                    setTimeout(function(){                        location.reload();                    },1000);                }            }        });    });    $('.status_update, .status_update_g').on('click',function(e){        e.preventDefault();        var id = $(this).data('id');        var status = $(this).data('value');        $.ajax({            type:'POST',            url:'<?php echo site_url('Users/updateStatus');?>'+'/'+id,            data:{'status':status},            success:function(response){                var data = $.parseJSON(response);                if(data['success']){                    new PNotify({                        title: 'کامیابی',                        text: 'اکاؤنٹ تدوین کامیاب',                        type: 'success',                        hide: true                    });                    setTimeout(function(){                        location.reload();                    },1000);                }            }        });    });//    $('.userDelete').on('click',function(e){//        e.preventDefault();//        var id = $(this).data('id');//        (//            new PNotify({//                title: 'تصدیق درکار',//                text: 'کیا